// DBManager 클래스 정의: 데이터베이스 연결 및 데이터 읽기 기능 제공
class DBManager {
  constructor() {
    this.dbConnector = new DBConnector();
    // 클래스 생성 시 데이터베이스에 연결
    this.dbConnector.connect();
  }

  // 판매 데이터 읽기 함수
  async readSalesData() {
    try {
      // 데이터베이스에서 판매 데이터 읽기
      const salesData = await this.dbConnector.readSalesData();
      return salesData;
    } catch (error) {
      throw new Error(`쿼리 실행 중 오류 발생: ${error}`);
    }
  }

  // 데이터베이스 연결 닫기 함수
