import DBConnector from "./DBConnector.js";
import ReadData from "./modules/manipulation/ReadData.js";
import DBCloser from "./DBCloser.js";

interface SaleItem {
  name: string;
  price: number;
}

// 판매 보고서 생성 및 출력 함수
async function generateSalesReport() {
  const readData = new ReadData();
  const dbCloser = new DBCloser();

  try {
    // 데이터베이스에서 판매 데이터 읽기
    const salesData: SaleItem[] = await readData.readSalesData();

    // 총 매출 계산
    const totalRevenue: number = salesData.reduce((total, item) => total + item.price, 0);

    // 가장 많이 팔린 제품 계산
    const productCount: { [key: string]: number } = {};
    salesData.forEach((item) => {
      const { name } = item;
      productCount[name] = (productCount[name] || 0) + 1;
    });

    // 가장 많이 팔린 제품 찾기
    let mostSoldProduct: string = '';
    let maxCount = 0;
