    const requestOptions = {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            productId: productId,
            quantity: quantity
        })
    };

    try {
        const response = await fetch('/api/purchase', requestOptions);

        // 응답 성공 여부 확인
        if (!response.ok) {
            throw new Error('서버 오류: ' + response.statusText);
        }

