        }
        console.log('구매 성공');
    } catch (error) {
        console.error('오류 발생:', error);
    }
}

// 구매하기 버튼에 click 이벤트 핸들러 등록
document.getElementById('purchaseBtn').addEventListener('click', sendPurchaseRequest);
```

