import express from "express";
import path from "path";

const app = express();

// ESM 방식의 __dirname 재정의
const __dirname = path.resolve();

// 미들웨어로 등록하기 위한 경로 설정
const publicPath = path.join(__dirname, "public");
const srcPath = path.join(__dirname, "src");

