      },
      body: JSON.stringify({
          product: 'exampleProduct',
          name: 'exampleName',
          price: 1000
      })
  };

try {// ?'/api/purchase' 변경해야함
  const response = fetch('/api/purchase', requestOptions);
  // 우선 모르는 다 표기 ok - 응답 성공 200~290
  if (!response.ok) {
    throw new Error('서버 오류: ' + response.statusText);
  }
  console.log('구매 성공');
  } catch (error) {
    console.error('오류 발생:', error);
  }
}이걸 테스트하게 만들어줘]

아래는 `sendPurchaseRequest` 함수를 테스트할 수 있는 간단한 ES Module 형식의 Node.js 서버 예제입니다. 이 예제는 Express를 사용하여 API 엔드포인트를 만들고, 클라이언트에서 `sendPurchaseRequest` 함수를 호출할 수 있도록 설정됩니다.

