### TypeScript 모듈 파일 (`productFunctions.ts`)

```typescript
// productFunctions.ts

// 제품 목록 배열 (임시 데이터)
const products = [
  { id: 1, name: '상품1', price: 1000 },
  { id: 2, name: '상품2', price: 2000 },
  { id: 3, name: '상품3', price: 3000 }
];

// 1. 제품 총액 계산 함수
export function calculateTotalPrice(selectedProducts: number[]): number {
  let totalPrice = 0;
  selectedProducts.forEach(productId => {
    const product = products.find(p => p.id === productId);
    if (product) {
      totalPrice += product.price;
    }
  });
  return totalPrice;
}

// 2. 총액 출력 함수
export function displayTotalAmount(totalAmount: number): void {
  const totalAmountElement = document.getElementById('totalAmount');
  if (totalAmountElement) {
    totalAmountElement.textContent = totalAmount.toString();
  }
}

// 3. 선택한 제품 출력 함수
export function displaySelectedProducts(selectedProducts: number[]): void {
  const selectedProductElement = document.getElementById('selectedProduct');
  if (selectedProductElement) {
    selectedProductElement.innerHTML = ''; // 기존 옵션 초기화
    selectedProducts.forEach(productId => {
      const product = products.find(p => p.id === productId);
      if (product) {
        const option = document.createElement('option');
        option.value = productId.toString();
        option.textContent = `${product.name} - ${product.price}원`;
        selectedProductElement.appendChild(option);
      }
    });
  }
}

// 이벤트 리스너 등록
document.addEventListener('DOMContentLoaded', () => {
  const depositBtn = document.getElementById('depositBtn');
  const selectProductBtn = document.getElementById('selectProductBtn');
  const purchaseBtn = document.getElementById('purchaseBtn');

  // 입금하기 버튼 클릭 시 예시
  if (depositBtn) {
    depositBtn.addEventListener('click', () => {
      const depositInput = document.getElementById('depositInput') as HTMLInputElement;
      const depositAmount = Number(depositInput.value);
      // 예시로 alert으로 출력
      alert(`입금액: ${depositAmount}원`);
    });
  }

  // 제품 선택 버튼 클릭 시 예시
  if (selectProductBtn) {
    selectProductBtn.addEventListener('click', () => {
      const selectedProductId = Number((document.getElementById('selectedProduct') as HTMLSelectElement).value);
      // 예시로 alert으로 출력
      alert(`선택한 제품 ID: ${selectedProductId}`);
    });
  }

  // 구매하기 버튼 클릭 시 예시
  if (purchaseBtn) {
    purchaseBtn.addEventListener('click', () => {
      // 예시로 alert으로 출력
      alert('구매 버튼 클릭됨');
    });
  }
});
