```typescript
// TypeScript 코드 예시

import sqlite3 from 'sqlite3';
import { open } from 'sqlite';

// SQLite 데이터베이스 파일 경로
const dbPath = 'path/to/your/database.db';

// SQLite 데이터베이스 연결 설정
async function openDatabase() {
    return open({
        filename: dbPath,
        driver: sqlite3.Database
    });
}

// 제품 판매 정보를 조회하는 함수
async function fetchSalesDataFromDB() {
    const db = await openDatabase();

    // 데이터베이스에서 판매 정보 조회
    const salesData = await db.all('SELECT name, price FROM sales');

    await db.close();

    return salesData;
}

// 제품 판매량 계산 함수
async function calculateProductSalesFromDB() {
    const salesData = await fetchSalesDataFromDB();
    const productCount = {};

    salesData.forEach(item => {
        const { name } = item;
        if (productCount[name]) {
            productCount[name]++;
        } else {
            productCount[name] = 1;
        }
    });

    return productCount;
}

// 예시 데이터베이스에서 제품 판매량 계산 및 출력
calculateProductSalesFromDB()
    .then(productSales => {
        console.log('제품 판매량:', productSales);

        // 가장 많이 팔린 제품 찾기
        let maxSales = 0;
        let mostSoldProduct = '';

        for (const productName in productSales) {
            if (productSales[productName] > maxSales) {
                maxSales = productSales[productName];
                mostSoldProduct = productName;
            }
        }

        console.log('가장 많이 팔린 제품:', mostSoldProduct);
    })
    .catch(error => {
        console.error('SQLite 데이터베이스에서 제품 판매 정보를 가져오는 도중 오류 발생:', error);
    });
```

