// 필요한 모듈 임포트
import sqlite3 from 'sqlite3';
import { open } from 'sqlite';

// SQLite 데이터베이스 파일 경로
const dbPath = 'path/to/your/database.db';

// SQLite 데이터베이스 연결 설정
async function openDatabase() {
    return open({
        filename: dbPath,
        driver: sqlite3.Database
    });
}

// 제품 판매 정보를 조회하는 함수
async function fetchSalesDataFromDB() {
    const db = await openDatabase();

    // 데이터베이스에서 판매 정보 조회
    const salesData = await db.all('SELECT name, price FROM sales');

    await db.close();

    return salesData;
}

// 총 매출 계산 함수
async function calculateTotalRevenue() {
    const salesData = await fetchSalesDataFromDB();
    let totalRevenue = 0;

    salesData.forEach(item => {
        totalRevenue += item.price;
    });

    return totalRevenue;
}

// 가장 많이 팔린 제품 계산 함수
async function calculateMostSoldProduct() {
    const salesData = await fetchSalesDataFromDB();
    const productCount = {};

    salesData.forEach(item => {
        const { name } = item;
        if (productCount[name]) {
            productCount[name]++;
        } else {
            productCount[name] = 1;
        }
    });

    // 가장 많이 팔린 제품 찾기
    let maxSales = 0;
    let mostSoldProduct = '';

    for (const productName in productCount) {
        if (productCount[productName] > maxSales) {
            maxSales = productCount[productName];
            mostSoldProduct = productName;
        }
    }

    return mostSoldProduct;
}

// 예시 데이터베이스에서 총 매출과 가장 많이 팔린 제품 출력
async function generateSalesReport() {
    try {
        const totalRevenue = await calculateTotalRevenue();
        console.log('총 매출:', totalRevenue);

        const mostSoldProduct = await calculateMostSoldProduct();
        console.log('가장 많이 팔린 제품:', mostSoldProduct);
    } catch (error) {
        console.error('SQLite 데이터베이스에서 제품 판매 정보를 가져오는 도중 오류 발생:', error);
    }
}

// 보고서 출력 버튼 클릭 이벤트 등에서 호출할 함수
generateSalesReport();
