class DBManager {
  private db: sqlite3.Database | null = null;

  constructor() {
    // 클래스 생성 시 데이터베이스에 연결
    this.connect();
  }

  // 데이터베이스 연결 함수
  private connect(): void {
    this.db = new sqlite3VM.Database(dbPath, sqlite3.OPEN_READONLY, (err) => {
      if (err) {
        console.error('SQLite 데이터베이스 연결 실패:', err.message);
