        return;
      }

      const sql = 'SELECT product AS name, price FROM product_list';
      this.db.all(sql, [], (err, rows) => {
        if (err) {
          reject(new Error(`쿼리문 조회 에러: ${err.message}`));
        } else {
          resolve(rows);
        }
      });
    });
  }

  // 데이터베이스 연결 닫기 함수
  close(): void {
