      const sql = `SELECT name, price FROM ${tableName}`;
      this.db.all(sql, [], (err, rows) => {
        if (err) {
          reject(new Error(`쿼리문 조회 에러: ${err.message}`));
        } else {
          resolve(rows);
        }
      });
    });
  }

  close(): void {
    if (this.db) {
      this.db.close((err) => {
        if (err) {
          console.error('데이터베이스 연결 닫기 실패:', err.message);
        } else {
          console.log('SQLite 데이터베이스 연결 닫힘');
        }
      });
    }
  }
}

