// 숫자를 증가시키는 함수
const incrementNumber = (start, end, step, callback) => {
    let current = start;

    const increment = () => {
        if (current <= end) {
            console.log(current); // 현재 숫자 출력
            current += step;
            setTimeout(increment, 500); // 500ms 후에 다시 호출
        } else {
            if (callback) callback(); // 작업이 완료되면 콜백 함수 호출
        }
    };

    increment(); // 작업 시작
};

// 콜백 함수
