### 서버 측 설정

SQLite3와 Express.js를 사용하여 서버 측에서 CRUD 작업을 처리하는 기본 코드를 제공하겠습니다. 먼저 `server.js` 파일을 만듭니다.

```javascript
const express = require('express');
const sqlite3 = require('sqlite3').verbose();
const bodyParser = require('body-parser');
const app = express();
const port = 3000;

// SQLite3 데이터베이스 연결
let db = new sqlite3.Database(':memory:');

app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended: true }));

// 테이블 생성
db.serialize(() => {
    db.run("CREATE TABLE records (name TEXT, text TEXT)");
});

// CORS 설정 (필요시)
app.use((req, res, next) => {
    res.header("Access-Control-Allow-Origin", "*");
    res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept");
    next();
});

// Create
app.post('/create', (req, res) => {
    const { name, text } = req.body;
    db.run("INSERT INTO records (name, text) VALUES (?, ?)", [name, text], function(err) {
        if (err) {
            return res.status(500).json({ error: err.message });
        }
        res.json({ id: this.lastID });
    });
});

// Read
app.get('/read', (req, res) => {
    db.all("SELECT * FROM records", [], (err, rows) => {
        if (err) {
            return res.status(500).json({ error: err.message });
        }
        res.json({ records: rows });
    });
});

// Update
app.post('/update', (req, res) => {
    const { name, text } = req.body;
    db.run("UPDATE records SET text = ? WHERE name = ?", [text, name], function(err) {
        if (err) {
            return res.status(500).json({ error: err.message });
        }
        res.json({ changes: this.changes });
    });
});

// Delete
app.post('/delete', (req, res) => {
    const { name } = req.body;
    db.run("DELETE FROM records WHERE name = ?", name, function(err) {
        if (err) {
            return res.status(500).json({ error: err.message });
        }
        res.json({ changes: this.changes });
    });
});

app.listen(port, () => {
    console.log(`Server running at http://localhost:${port}`);
});
```

