                body: JSON.stringify(dreams)
            });
            if (response.ok) {
                alert('Purchase successful!');
                document.getElementById('selected-dream-list').innerHTML = '';
            } else {
                alert('Purchase failed.');
            }
        } else {
            alert('Not enough balance.');
        }
    });
});
