```typescript
import sqlite3 from "sqlite3";
import { openDatabase } from "./path/to/openDatabase"; // 이 줄을 나중에 정의합니다

// 데이터베이스에서 판매 데이터 가져오기 함수
async function fetchSalesDataFromDB(): Promise<{ name: string, price: number }[]> {
    const db = await openDatabase();
    return new Promise((resolve, reject) => {
        db.all('SELECT name, price FROM sales', (err, rows) => {
            if (err) {
                reject(err);
            } else {
                resolve(rows);
            }
            db.close();
        });
    });
}

// 총 매출 계산 함수
async function calculateTotalRevenue(): Promise<number> {
    const salesData = await fetchSalesDataFromDB();
    return salesData.reduce((total: number, item: { name: string, price: number }) => total + item.price, 0);
}

// 가장 많이 팔린 제품 계산 함수
async function calculateMostSoldProduct(): Promise<string> {
    const salesData = await fetchSalesDataFromDB();
    const productCount: { [key: string]: number } = {};

    // 각 제품의 판매 횟수 계산
    salesData.forEach((item: { name: string, price: number }) => {
        const { name } = item;
        productCount[name] = (productCount[name] || 0) + 1;
    });

    // 제품 이름과 판매 횟수를 배열로 변환
    const productCountArray = Object.entries(productCount);

    // 판매 횟수를 기준으로 배열 정렬
    productCountArray.sort((a, b) => b[1] - a[1]);

    // 가장 많이 팔린 제품의 이름 반환
    const mostSoldProduct = productCountArray[0][0];

    return mostSoldProduct;
}

// 판매 보고서 생성 및 출력 함수
async function generateSalesReport(): Promise<void> {
    try {
        const totalRevenue = await calculateTotalRevenue();
        console.log('총 매출:', totalRevenue);

        const mostSoldProduct = await calculateMostSoldProduct();
        console.log('가장 많이 팔린 제품:', mostSoldProduct);
    } catch (error) {
        console.error('SQLite 데이터베이스에서 제품 판매 정보를 가져오는 도중 오류 발생:', error);
    }
}

// 보고서 출력
generateSalesReport();

// openDatabase 함수 정의
export async function openDatabase(): Promise<sqlite3.Database> {
    return new Promise((resolve, reject) => {
        const db = new sqlite3.Database('path/to/your/database.db', (err) => {
            if (err) {
                reject(err);
            } else {
                resolve(db);
            }
        });
    });
}
