import sqlite3, { Database as SQLite3Database } from "sqlite3";

const sqlite3VM = sqlite3.verbose();

interface ColumnDefinition {
  [key: string]: string;
}

interface Record {
  [key: string]: string | number | boolean | null;
}

interface UpdateData {
  [key: string]: string | number | boolean | null;
}

export class BaseDataBaseManager {
  db: SQLite3Database;
  fileWithPath: string;

  constructor(fileWithPath: string) {
    this.fileWithPath = fileWithPath;
    this.db = new sqlite3VM.Database(fileWithPath, (err: Error | null) => {
      if (err) {
        throw new Error("DB 연결 실패");
      } else {
        console.log("DB 연결 성공");
      }
    });
  }

  // 데이터베이스에서 레코드 읽기 함수
  readRecord(
    tableName: string,
    column: string | number,
    value: string | number,
    log: boolean = false
  ): Promise<any[]> {
    const sql = `SELECT * FROM ${tableName} WHERE ${column} = ?`;
    return new Promise((resolve, reject) => {
      this.db.all(sql, [value], (err: Error | null, rows: any[]) => {
        if (err) {
          reject(new Error(`쿼리문 조회 에러: ${err.message}`));
        } else if (log) {
          console.log(rows);
          resolve(rows);
        } else {
          resolve(rows);
        }
      });
    });
  }

  // 총 매출 계산 함수
  calculateTotalRevenue(): Promise<number> {
    const sql = `SELECT SUM(price) AS totalRevenue FROM sales`;
    return new Promise((resolve, reject) => {
      this.db.get(sql, [], (err: Error | null, row: any) => {
        if (err) {
          reject(new Error(`총 매출 조회 에러: ${err.message}`));
        } else {
          resolve(row.totalRevenue || 0);
        }
      });
    });
  }

  // 가장 많이 팔린 제품 계산 함수
  calculateMostSoldProduct(): Promise<string> {
    const sql = `
      SELECT name, COUNT(name) AS salesCount
      FROM sales
      GROUP BY name
      ORDER BY salesCount DESC
      LIMIT 1
    `;
    return new Promise((resolve, reject) => {
      this.db.get(sql, [], (err: Error | null, row: any) => {
        if (err) {
          reject(new Error(`가장 많이 팔린 제품 조회 에러: ${err.message}`));
        } else {
          resolve(row ? row.name : '');
        }
      });
    });
  }
}

