```typescript
import sqlite3 from 'sqlite3';

const sqlite3VM = sqlite3.verbose();

// SQLite 데이터베이스 파일 경로
const dbPath = 'path/to/your/database.db';

// 총 매출 계산 함수
async function calculateTotalRevenue(): Promise<number> {
    return new Promise((resolve, reject) => {
        const db = new sqlite3VM.Database(dbPath, sqlite3.OPEN_READONLY, (err) => {
            if (err) {
                reject(new Error('SQLite 데이터베이스 연결 실패'));
            } else {
                db.all('SELECT price FROM sales', (err, rows) => {
                    if (err) {
                        reject(new Error(`쿼리문 조회 에러: ${err.message}`));
                    } else {
                        const totalRevenue = rows.reduce((total, item) => total + item.price, 0);
                        resolve(totalRevenue);
                    }
                });
                db.close();
            }
        });
    });
}

// 가장 많이 팔린 제품 계산 함수
async function calculateMostSoldProduct(): Promise<string> {
    return new Promise((resolve, reject) => {
        const db = new sqlite3VM.Database(dbPath, sqlite3.OPEN_READONLY, (err) => {
            if (err) {
                reject(new Error('SQLite 데이터베이스 연결 실패'));
            } else {
                db.all('SELECT name, COUNT(*) AS salesCount FROM sales GROUP BY name ORDER BY salesCount DESC LIMIT 1', (err, rows) => {
                    if (err) {
                        reject(new Error(`쿼리문 조회 에러: ${err.message}`));
                    } else {
                        const mostSoldProduct = rows.length > 0 ? rows[0].name : '';
                        resolve(mostSoldProduct);
                    }
                });
                db.close();
            }
        });
    });
}

