  document.getElementById('remainingMoney').textContent = remainingMoney;
}

async function purchase() {
  const remainingMoney = localStorage.getItem('money') - totalSelectedPrice;
  localStorage.setItem('remainingMoney', remainingMoney);
  document.getElementById('remainingMoney').textContent = remainingMoney;

  const response = await fetch('/purchase', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify({ totalSelectedPrice })
  });
  const result = await response.json();
  if (result.success) {
    alert('Purchase successful!');
    await loadProducts();
  } else {
    alert(result.message);
  }
}
```

