### JavaScript 코드 (public/script.js)
주석을 추가하여 각 부분의 동작을 설명합니다.

```javascript
// 총 선택된 제품의 가격을 저장하는 변수
let totalSelectedPrice = 0;

// 사용자가 입력한 금액을 저장하는 변수
let insertedMoney = 0;

// 선택된 제품 목록을 저장하는 배열
let selectedProducts = [];

// DOMContentLoaded 이벤트가 발생하면 loadProducts 함수 호출
document.addEventListener('DOMContentLoaded', async () => {
  await loadProducts();
});

// 사용자가 입력한 금액을 처리하는 함수
async function handleMoneyInput() {
  // 입력한 금액을 가져와 정수로 변환
  insertedMoney = parseInt(document.getElementById('moneyInput').value);

  // 로컬 스토리지에 금액 저장
  localStorage.setItem('money', insertedMoney);

  // 남은 금액을 업데이트
  document.getElementById('remainingMoney').textContent = insertedMoney;

  // 제품 목록을 다시 로드
  await loadProducts();
}

// 서버에서 제품 목록을 가져와 화면에 표시하는 함수
async function loadProducts() {
  // 서버로부터 제품 목록을 가져옴
  const response = await fetch('/api/products');
  const products = await response.json();

  // 각 가격 범위에 해당하는 제품 리스트 요소를 가져옴
  const lists = {
    1000: document.getElementById('1000list'),
    5000: document.getElementById('5000list'),
    10000: document.getElementById('10000list')
  };

  // 각 리스트를 비움
  Object.values(lists).forEach(list => list.innerHTML = '');

  // 제품 목록을 순회하며 제품 요소를 생성하고 추가
  products.forEach(product => {
    const li = document.createElement('li');
    li.textContent = `${product.name} - ${product.price}원`;
    li.className = 'border-2 border-black p-2 cursor-pointer';
    li.dataset.price = product.price;
    li.dataset.name = product.name;

    // 제품 요소에 클릭 이벤트 핸들러를 추가
    li.onclick = () => selectProduct(product);

    // 제품 가격에 따라 해당 리스트에 추가
    if (product.price <= 1000) {
      lists[1000].appendChild(li);
    } else if (product.price <= 5000) {
      lists[5000].appendChild(li);
    } else if (product.price <= 10000) {
      lists[10000].appendChild(li);
    }
  });
}

// 사용자가 제품을 선택했을 때 호출되는 함수
function selectProduct(product) {
  // 선택한 제품의 가격을 총 선택 가격에 더함
  totalSelectedPrice += product.price;

  // 선택한 제품을 배열에 추가
  selectedProducts.push(product);

