        </div>
        <div>
            <input type="checkbox" id="product3" name="product" value="water">
            <label for="product3">생수 - 500원</label>
        </div>
    </div>

    <button onclick="calculateTotal()">금액 계산하기</button>
    <div id="total-price">
        <!-- 여기에 총액이 표시됩니다. -->
    </div>

    <script>
        // 가상의 제품 가격 정보 객체
        const productPrices = {
            cola: 1000,
            chips: 1500,
            water: 500
        };

        // 총액 계산 함수
        function calculateTotal() {
            let totalPrice = 0;
            const checkboxes = document.getElementsByName('product');
            checkboxes.forEach((checkbox) => {
