// pokemonsearch.js
module.exports = function(searchQuery) {
    return new Promise((resolve, reject) => {
        // Simulate a search operation
        const mockDatabase = {
            pikachu: { name: 'Pikachu', type: 'Electric' },
            charmander: { name: 'Charmander', type: 'Fire' },
            bulbasaur: { name: 'Bulbasaur', type: 'Grass/Poison' }
        };

        const result = mockDatabase[searchQuery.toLowerCase()];
        if (result) {
            resolve(result);
        } else {
            reject(new Error('Pokemon not found'));
        }
    });
};
